package com.stringdemo;

public class EndsWithDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

		String firstname="Aman Kumar Sharma";
	
		String Anothername = "ABHINAV SHARMA";
		
		
		//ENDS WITH return Boolean Value
		System.out.println(firstname.endsWith("a"));//returns True
		System.out.println(firstname.endsWith(Anothername));//returns false
	}

}
